import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import TextField from 'material-ui/TextField';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import {Link} from 'react-router-dom';
import Calendar from 'react-calendar';
import RaisedButton from 'material-ui/RaisedButton';
import ListOfTasks from './ListOfTasks'
import axios from 'axios';


const initialState = {
    Task: '',
    Priority: '',
    Parent_Task:  '',
    Start_Date:'',
    End_Date:'',    
}

export class AddTask extends Component {
    
    constructor(props) {
        super(props)

        this.state = {
            initialState, 
            Priority: '5',
            Parent_Task: 'Parent Task 1',
            // date: new Date(),
            // Start_Date: new Date(),
            // End_Date: new Date(),

            personalData: {  
                Task: '',
                Priority: '5',
                Parent_Task:  'Parent Task 1',
                Start_Date: new Date(),
                End_Date: new Date(),
       } 
        }
        this.handleChange = this.handleChange.bind(this)
    }

handleChange = (event) =>{
        const { name, value } = event.target;
        let { personalData } = this.state;
        let userData = {  
            ...personalData,
            [name]: value,
        }
        this.setState({
             [name]: value,
             personalData :userData
         })
   }

handleUpdateChange = (event) =>{

    console.log(this.state.personalData)
  
    const {Task,Priority,Parent_Task,Start_Date,End_Date}= this.state.personalData;

   
    const formValues = {
        task:  Task,
        priority: Priority,
        parentTask: Parent_Task,
        startDate: Start_Date,
        endDate: End_Date,
        }
   
     
        console.log("----------------------------")
        axios({
         method: 'post',
         url: 'http://localhost:8098/Add',
         data: { 
            task:  Task,
            priority: Priority,
            parentTask: Parent_Task,
            startDate: Start_Date,
            endDate: End_Date,
            },
         headers: {
             'content-type': `application/json;`
         }
     }).then(response =>{
         console.log(response.data)
         if(response.data){
             alert("Registered Successfully")
          
            this.props.history.push('/ListOfTasks')
         
         }
     });
    }
   

 
    render()
     {


       

        return (
<div>
    <MuiThemeProvider>
        <div>
            <div class="row">
        <div class="row col-md-6">
                    <nav className="navbar navbar-primary bg">
                    <a id= "color_login" className="navbar-brand navLogin"></a>
                        <form className="form-inline">                      
                        <ul class="list-group">                              
                              <li class="list-group-item"><Link to = '/AddTask'> <button className="btn my-2 my-sm-0" type="submit">Add Task</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                              <li class="list-group-item"><Link to = '/ListOfTasks'> <button className="btn my-2 my-sm-0" type="submit">View Task</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                        </ul>                      
                        </form>
                    </nav>
                </div>
                <div>
                    <nav className="navbar navbar-primary bg">
                    <a id= "color_login" className="navbar-brand navLogin">Add Task</a>
                        <form className="form-inline">                        
                        <Link to = '/Home'> <button className="btn my-2 my-sm-0" type="submit">Home</button></Link>
                      
                        </form>
                    </nav>
                </div>
</div>
                <div>
                        <TextField
                        name="Task"
                        type="text"
                        hintText="Enter the specified Task"
                        floatingLabelText="Task*"
                        onChange = {this.handleChange}
                        />
                </div>

             <div className="marginTask">
                <div name="Priority"class="input-group mb-3">
                        <div name="Priority" class="input-group-prepend">
                            <label name="Priority" class="input-group-text" for="inputGroupSelect01">Priority </label>
                        </div>
                        <select id="width" name="Priority" class="custom-selects" id="inputGroupSelect01" value={this.state.Priority} onChange={this.handleChange}>
                        
                            <option  value="0" selected>0</option>
                            <option  value="5">5</option>
                            <option  value="10">10</option>
                            <option  value="15">15</option>
                            <option  value="20">20</option>
                            <option  value="25">25</option>
                            <option  value="30">30</option>

                        </select>
                </div>
                <br/>



                 <div name="Start_Date"class="input-group mb-3">
                        <div name="Start_Date" class="input-group-prepend">
                            <label name="Start_Date" class="input-group-text" for="inputGroupSelect01">Start Date</label>
                        </div>
                        <input type="Date" name="Start_Date" class="custom-selects1" id="inputGroupSelect01" value={this.state.Start_Date}
                                onChange={this.handleChange} ></input>
                 </div>
                 <br/>




                <div name="End_Date"class="input-group mb-3">
                    <div name="End_Date" class="input-group-prepend">
                        <label name="End_Date" class="input-group-text" for="inputGroupSelect01">End Date</label>
                    </div>
                    <input type="Date" name="End_Date" class="custom-selects2" id="inputGroupSelect01" value={this.state.End_Date}
                        onChange={this.handleChange}     ></input>
                </div>
                <br/>




                <div name="Parent_Task"class="input-group mb-3">
                    <div name="Parent_Task" class="input-group-prepend">
                        <label name="Parent_Task" class="input-group-text" for="inputGroupSelect01">Parent Task</label>
                    </div>
                    <select id="width" name="Parent_Task" class="custom-selects3" id="inputGroupSelect01" value={this.state.Parent_Task} onChange={this.handleChange}> 
                        <option  value="Parent Task 1" selected>Parent Task 1</option>
                        <option  value="Parent Task 2">Parent Task 2</option>
                        <option  value="Parent Task 3">Parent Task 3</option>
                        <option  value="Parent Task 4 ">Parent Task 4</option>
                    </select>
                </div>
                <br/>
             </div>

                    <button type="button" class="btn btn-info" onClick= {this.handleUpdateChange}>Save</button>&nbsp;&nbsp;&nbsp;&nbsp;
  
         </div>
    </MuiThemeProvider>
</div>
        )
    }
}

export default AddTask;
