import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import TextField from 'material-ui/TextField';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import {Link} from 'react-router-dom';
import Calendar from 'react-calendar';
import RaisedButton from 'material-ui/RaisedButton';
import ViewTasks from './ViewTasks'
import axios from 'axios';


const initialState = {
    Task: '',
    Priority: '',
    Parent_Task:  '',
    Start_Date:'',
    End_Date:'',    
}

export class AddUser extends Component {
    
    constructor(props) {
        super(props)

        this.state = {
            initialState, 
            Priority: '5',
            Parent_Task: 'Parent Task 1',
            // date: new Date(),
            // Start_Date: new Date(),
            // End_Date: new Date(),

            personalData: {  
                firstName: '',
                lastName: '',
                employeeID:  '',  
                   } 
        }
        this.handleChange = this.handleChange.bind(this)
    }


   

handleChange = (event) =>{
        const { name, value } = event.target;
        let { personalData } = this.state;
        let userData = {  
            ...personalData,
            [name]: value,
        }
        this.setState({
             [name]: value,
             personalData :userData
         })
   }

handleUpdateChange = (event) =>{

    console.log(this.state.personalData)
  
    const {firstName,lastName,employeeID}= this.state.personalData;

   
    const formValues = {
        firstName:  firstName,
        lastName: lastName,
        employeeID: employeeID,
        }
   
     
        console.log("----------------------------")
        axios({
         method: 'post',
         url: 'http://localhost:8098/Add/User',
         data: { 
            firstName:  firstName,
            lastName: lastName,
            employeeID: employeeID,
            },
         headers: {
             'content-type': `application/json;`
         }
     }).then(response =>{
         console.log(response.data)
         if(response.data){
             alert("Registered Successfully")
          
             this.props.history.push('/UserTasks')
         
         }
     });
    }
   

 
    render()
     {


       

        return (
<div>
    <MuiThemeProvider >
    <div>
        <div className="row col-md-12">
            <h3 style={{marginTop: "15px",marginLeft: "30px"}}>Task Manager</h3>
            </div>
            <div className="row">
        <div className="row col-md-3">
                    <nav className="navbar navbar-primary bg">
                    <a id= "color_login" className="navbar-brand navLogin"></a>
                        <form className="form-inline">                      
                        <ul className="list-group">                              
                              <li className="list-group-item"><Link to = '/AddTask'> <button className="btn my-2 my-sm-0" type="submit">Add Task</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                              <li className="list-group-item"><Link to = '/ViewTasks'> <button className="btn my-2 my-sm-0" type="submit">View Tasks</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                              <li className="list-group-item"><Link to = '/AddProject'> <button className="btn my-2 my-sm-0" type="submit">Add Project</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                              <li className="list-group-item"><Link to = '/AddUser'> <button className="btn my-2 my-sm-0" type="submit">Add User</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                        </ul>                      
                        </form>
                    </nav>
                </div>
                <div  class="col-md-9">
                    <nav className="navbar navbar-primary bg">
                    <a id= "color_login" className="navbar-brand navLogin">Add User</a>
                        <form className="form-inline">
                          <Link to = '/UserTasks'> <button className="btn btn-success my-2 my-sm-0" type="submit">User Details</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                        <Link to = '/Home'> <button className="btn btn-success my-2 my-sm-0" type="submit">Home</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                     
                        </form>
                    </nav>
                    </div>
</div>


<div className="row">
<div className="col-md-3">
</div>
<div className="col-md-9">
<div className="row col-md-12">
                <div name="task" className="input-group-prepend">
                <span style={{marginTop: "39px"}}>First Name</span>&nbsp;&nbsp;
                        </div>

                        <TextField
                        name="firstName"
                        type="text"
                        hintText="Enter your First Name"
                        floatingLabelText="First Name*"
                        onChange = {this.handleChange}
                        style={{width: "275px",marginLeft: "28px"}}
                        />
                </div>
                <br/>
                <div className="row col-md-12">
                <div name="task" className="input-group-prepend">
                <span style={{marginTop: "39px"}}>Last Name</span>&nbsp;&nbsp;
                        </div>
                        <TextField
                        name="lastName"
                        type="text"
                        hintText="Enter your Last Name"
                        floatingLabelText="Last Name*"
                        onChange = {this.handleChange}
                        style={{width: "275px",marginLeft: "28px"}}
                        />
                </div>
                <br/>
                <div className="row col-md-12">
                <div name="task" className="input-group-prepend">
                <span style={{marginTop: "39px"}}>Employee ID</span>&nbsp;&nbsp;
                        </div>
                        <TextField
                        name="employeeID"
                        type="text"
                        hintText="Enter your Employee ID"
                        floatingLabelText="Emp ID*"
                        onChange = {this.handleChange}
                        style={{width: "275px",marginLeft: "15px"}}
                        />
                </div>
</div>
</div>



                
              

                    <br/>
                    <br/>
<div>
                    <button type="button" style={{marginLeft: "197px"}} class="btn btn-success" onClick= {this.handleUpdateChange}>Save</button>&nbsp;&nbsp;&nbsp;&nbsp;
                    </div>
         </div>
    </MuiThemeProvider>
</div>
        )
    }
}

export default AddUser;
