import React, { Component } from 'react';
import axios from 'axios';
import UpdateTask from './UpdateTask';
import {Link} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
export class ListOfTasks extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
          
            lists:[]
        }
    }
    

    handleDeleteChange = (event) =>{
       
      
        alert("delete")
        //   let id =event.target.id;
        //   console.log(id)
         let task =event.target.id;
         console.log(task)
           
    
             const urlUpdate = `http://localhost:8098/Delete/${task}`;
              //  const urlUpdate = `http://localhost:8098/Del/${id}`;
              console.log(urlUpdate)
              axios.delete(urlUpdate).then(response => response.data)
              .then((data) => {
                  console.log(data)
                  this.setState({ 
                      lists:data
          
          
                  }
                  )
               })
          
      console.log(event.target.id);
      }

    //   handleDeleteChange = (event) =>{
       
      
    //     alert("delete")
    //       let task =event.target.id;
    //       console.log(task)
        

    //       const urlUpdate = `http://localhost:8098/DelByTask/${task}`;
    //           console.log(urlUpdate)
    //           axios.delete(urlUpdate).then(response => response.data)
    //           .then((data) => {
    //               console.log(data)
    //               this.setState({ 
    //                   lists:data
          
          
    //               }
    //               )
    //            })
          
    //   console.log(event.target.id);
    //   }

    handleUpdateChange = (event) =>{
      
       
        const Task_ID = event.target.id;
        console.log(Task_ID)
        const v = `http://localhost:8098/${Task_ID}`
        console.log(v)
        axios.get(`http://localhost:8098/${Task_ID}`)
            .then(response => response.data)
            .then((data) => {
             console.log(data)
             const formValues = {
                Task_ID:Task_ID,
                Task: data.task,
                Priority: data.priority,
                Parent_Task: data.parentTask,
                Start_Date:data.startDate,
                End_Date:data.endDate,
                }
                console.log(data)
                console.log(this.state.lists)
                console.log("-----------------")
                this.props.history.push({pathname:'/UpdateTask',
                state:{response:formValues}})
                })
        // this.props.history.push('/UpdateTask')
       
    }
    renderTableData() {
        console.log(this.state.lists)
        console.log(this.state.lists[0])
   return this.state.lists.map((list,index) => {
 
        const {task,priority,parentTask,startDate,endDate,task_ID} = list //destructuring
      
      return (

          
         <tr key={task_ID}>
            <td>{task_ID}</td>
            <td>{task}</td>
            <td>{priority}</td>
            <td>{parentTask}</td>
            <td>{startDate}</td>
            <td>{endDate}</td>
            <td><button type="button" id={task_ID} class="btn btn-info" onClick= {this.handleUpdateChange}>Edit Task</button></td> 
            <td><button type="button"  id={task}  class="btn btn-danger" onClick={this.handleDeleteChange} >Delete</button></td>

         </tr>
         
      )
   })
}

componentDidMount()  {
  
   
    console.log("Inside component did mount")
{


axios.get(`http://localhost:8098/Details`)
.then(response => response.data)
.then((data) => {
  console.log(data)
  this.setState({ 
      lists:data
  }
  )
  console.log(data)
  console.log(this.state.lists)
  console.log("-----------------")

})
}
}
    render() {
        return (
            <div>
                  <MuiThemeProvider>
                      <div class="row">
                  <div class="col-md-3">
                    <nav className="navbar navbar-primary bg">
                    <a id= "color_login" className="navbar-brand navLogin"></a>
                        <form className="form-inline">                      
                        <ul class="list-group">                              
                              <li class="list-group-item"><Link to = '/ListOfTasks'> <button className="btn my-2 my-sm-0" type="submit">Task Details</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;</li>
                        </ul>                      
                        </form>
                    </nav>
                </div>
                  <div  class="col-md-9">
                    <nav className="navbar navbar-primary bg">
                    <a id= "color_login" className="navbar-brand navLogin">List of Tasks</a>
                        <form className="form-inline">
                        <Link to = '/AddTask'> <button className="btn my-2 my-sm-0" type="submit">Add Task</button></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                        <Link to = '/Home'>  <button className="btn my-2 my-sm-0" type="submit">Home</button></Link>
                        </form>
                    </nav>
                </div>
                </div>
                 <div className="margin_project"> 
                <br/>
             
              <br/>
              <table id='students'>
                 <thead>
                     <tr>
                         <th>Task_ID</th>
                         <th>Task</th>
                         <th>Parent Task</th>
                         <th>Priority</th>
                         <th> Start date </th>
                         <th> End date   </th>
                         <th colspan="2" >Action</th>
                         </tr>
                     </thead>
                 <tbody>
                                       
                   {this.renderTableData()}
                 </tbody>
              </table>
           </div>
           </MuiThemeProvider>
            </div>
        )
    }
}

export default ListOfTasks
